import './Profile.css'
import {useStudentContext} from '../../../context/Context';


const Profile = () => {
    const context = useStudentContext();
    return (
        <div className={'Profile'}>
            <img id={'ProfileImg'} src={context.nowStudentData.profileImg}/>
            <ul className={'DetailContent'}>
                <li className={'Line'} id={'Name'}>
                    이름 <input disabled={true} className={'DetailInput'} value={context.nowStudentData.name}/>
                </li>
                <li className={'Line'} id={'Grade'}>
                    학년 <input className={'DetailInput'} value={context.nowStudentData.grade}/>
                </li>
            </ul>

        </div>
    )
}



export default Profile
