import './DashBoard.css'
import {useStudentContext} from '../../context/Context';
import {Pie, PieChart, Tooltip} from 'recharts';
import {useState, useEffect} from 'react';


const DashBoard = () => { //다음 기회에
    const context = useStudentContext();

    const percentage = (i) => {
        return Math.round((context.studentData.filter(student => student.grade === i).length) * 100 / context.studentData.length)
    }
    const [dashBoardData, setDashBoardData] = useState(context.studentData.length === 0 ? null : [{
        name: '1st',
        value: percentage(1)
    }, {name: '2nd', value: percentage(2)}, {name: '3rd', value: percentage(3)}]);
    useEffect(() => {
        console.log('HI');
        setDashBoardData(context.studentData.length === 0 ? null : [{
            name: '1st',
            value: percentage(1)
        }, {name: '2nd', value: percentage(2)}, {name: '3rd', value: percentage(3)}]);
    },[context.studentData]);


    return (
        <div className={'DashBoard'}>
            <div id={'DashBoard_Text'}>
                여기엔 자유롭게 대시보드를 만들어 주세요
            </div>
            {context.studentData.length === 0 ? null : <PieChart width={400} height={174}>
                <Pie data={dashBoardData} dataKey="value" cx="50%" cy="50%" outerRadius={80}
                     fill="#8884d8"/>
                <Tooltip/>
            </PieChart>}

        </div>
    )
}

export default DashBoard
