import './DashBoard.css'
import {useStudentContext} from '../../context/Context';
import {Pie, PieChart} from 'recharts';

const DashBoard = () => { //다음 기회에
    const context = useStudentContext();
    const data = context.studentData.length === 0 ? null : [{name:'1st', key:context.studentData.filter(student=>student.grade===1).length}, {name:'2nd', key: context.studentData.filter(student=>student.grade===2).length}, {name:'3rd', key: context.studentData.filter(student=>student.grade===3).length}]
    return (
        <div className={'DashBoard'}>
            <div id={'DashBoard_Text'}>
                여기엔 자유롭게 대시보드를 만들어 주세요
            </div>
            {context.studentData.length === 0 ? null : <PieChart width={400} height={174}>
                <Pie data={data} dataKey="key" nameKey="name" cx="50%" cy="50%" outerRadius={80}
                     />
            </PieChart>}

        </div>
    )
}

export default DashBoard
